// DATETIME_FORMAT
// 0: epoch
// 1: string - "."
// 2: string - ","
datetime_format = env.get("DATETIME_FORMAT");

if (datetime_format == 1) {
    datetime_value = Date.now();
} else if (datetime_format == 2) {
    d = new Date();
    datetime_value = (d.getFullYear() + '-' + ('00' + (d.getMonth()+1)).slice(-2) + '-' + ('00' + d.getDate()).slice(-2) + ' ' + ('00' + d.getHours()).slice(-2) + ':' + ('00' + d.getMinutes()).slice(-2) + ':' + ('00' + d.getSeconds()).slice(-2) + '.' + ('000' + d.getMilliseconds()).slice(-3));
} else if (datetime_format == 3) {
    d = new Date();
    datetime_value = (d.getFullYear() + '-' + ('00' + (d.getMonth()+1)).slice(-2) + '-' + ('00' + d.getDate()).slice(-2) + ' ' + ('00' + d.getHours()).slice(-2) + ':' + ('00' + d.getMinutes()).slice(-2) + ':' + ('00' + d.getSeconds()).slice(-2) + ',' + ('000' + d.getMilliseconds()).slice(-3));
} else {
    node.error("Invalid datetime format");
    return null;
}

var pos = values.indexOf(env.get("DATETIME_NAME"));
if (pos !== -1) {
    values[pos] = datetime_value;
} else {
    node.error("Datetime column not present");
    return null;
}

msg.payload = values;

return msg;

status = msg.status;

if (status.text == "active") {
    node.debug("Client connected to server");
    return [msg, msg];
} else if (status.text == "connecting") {
    node.debug("Client disconnected from server");
    return [null, msg];
} else if (status.text.includes("listening")) {
    node.debug("Server is listening");
    return [null, msg];
} else {
    node.warn(status.text);
    return [null, msg];
}

