};

return msg;

var timer = context.get("timer") || null;
const connectionTimeoutMessage = 5000;

if (!msg.hasOwnProperty("status")) {
    // Inicialization message, timer must be started
    msg = {};
    tTimer = setTimeout(function() {
                node.error("Connection to OPC UA Server cannot be stablished, check the configuration parameters");
                timer = null;
                msg.status = {fill:"red",shape:"dot",text:"Error (see debug message)"};
                node.send(msg);
            }, connectionTimeoutMessage);
    context.set("timer", tTimer);
    msg.status = {fill:"yellow",shape:"dot",text:"Connecting..."};
    return msg;
} else {
    state = msg.status.text;
    if (state == "active") {
        // Connection done, stop the timer
        node.debug("Connection to server stablished");
        if (timer !== null) {
            clearTimeout(timer);
            timer = null;
            context.set("timer", timer);
        }
        return msg;
    }
}

